// src/components/LivroLista.tsx
import React, { useState, useEffect } from 'react';
import ControleLivros from '../controle/ControleLivros';
import ControleEditora from '../controle/ControleEditora';
import Livro from '../modelos/Livro';

const LinhaLivro = ({ livro, excluir }: { livro: Livro, excluir: (codigo: number) => void }) => {
  const nomeEditora = ControleEditora.getNomeEditora(livro.codEditora);

  const handleExcluir = () => {
    excluir(livro.codigo);
  };

  return (
    <tr>
      <td>{livro.codigo}</td>
      <td>{livro.titulo}</td>
      <td>{nomeEditora}</td>
      <td>{livro.resumo}</td>
      <td>
        <button onClick={handleExcluir}>Excluir</button>
      </td>
      <td>
        <ul>
          {livro.autores.map((autor, index) => (
            <li key={index}>{autor}</li>
          ))}
        </ul>
      </td>
    </tr>
  );
};

const LivroLista = () => {
  const [livros, setLivros] = useState<Livro[]>([]);
  const [carregado, setCarregado] = useState<boolean>(false);

  useEffect(() => {
    setLivros(ControleLivros.obterLivros());
    setCarregado(true);
  }, [carregado]);

  const handleExcluir = (codigo: number) => {
    ControleLivros.excluir(codigo);
    setCarregado(false);
  };

  return (
    <main>
      <h1>Lista de Livros</h1>
      <table>
        <thead>
          <tr>
            <th>Código</th>
            <th>Título</th>
            <th>Editora</th>
            <th>Resumo</th>
            <th>Excluir</th>
            <th>Autores</th>
          </tr>
        </thead>
        <tbody>
          {livros.map((livro) => (
            <LinhaLivro key={livro.codigo} livro={livro} excluir={handleExcluir} />
          ))}
        </tbody>
      </table>
    </main>
  );
};

export default LivroLista;
